mapper.update:
update member set phone=#{phone}, zipcode=#{zipcode},
address1=#{address1},
address2=#{address2}, email=#{email}
where
id=#{id} and name=#{name}

mapper.select:
select * from (select locketlist.*,row_number() over(order
by homename desc) rnum from locketlist where id=#{id}) where rnum between ${start}
and ${end}
